package 변수;

public class 변수선언확인문제 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 7;
		double num2 = 2.4;
		char gender = '여';
		boolean food = false;
		
		String name = "홍길동"; 
		//여러 글자를 쓸 떄 ""자
		//string의 S는 대문자
		
		System.out.println("정수값은" + num);
		System.out.println("실수값은" + num2);
		System.out.println("문자값은" +" " + gender);
		System.out.println("논리값은" + " " + food);
		System.out.println("이름은" + " " + name);
		
	}

}
